Item.method_code:
string url= this.getProperty("url","http://vdcwl481.vdcham.tsg.ge.com/MBOM_dev");
WebRequest rq = (HttpWebRequest)WebRequest.Create(url+"/Server/DBList.aspx");
rq.Method = "GET";
rq.ContentType = "text/xml; encoding='utf-8'";
WebResponse resp = rq.GetResponse();
            // Get the stream containing content returned by the server.
            Stream dataStream = resp.GetResponseStream ();
            // Open the stream using a StreamReader for easy access.
            StreamReader reader = new StreamReader (dataStream);
            // Read the content.
            string responseFromServer = reader.ReadToEnd ();
            // Display the content.
return this.getInnovator().newResult(responseFromServer);
