Item.method_code:
Innovator innov=this.getInnovator();
string qry1=@"
<AML>
<Item type=""UserStory"" select=""classification,item_number,name,userstory_type, as_a,i_want_to,so_that,user_steps,test_plan_sort"" action=""GetItemRepeatConfig"" id=""{0}"">
	<Relationships>
		<Item type=""UserStoryRelated"" repeatProp=""related_id"" select=""sort_order,related_id"" repeatTimes=""0"" />
	</Relationships>
</Item>
</AML>
";
qry1=string.Format(qry1,this.getID());
Item res1=innov.applyAML(qry1);
I18NSessionContext cntx = innov.getI18NSessionContext();
DateTime utc_now=DateTime.UtcNow;
//DateTime corp_now=utc_now.AddMinutes(cntx.GetCorporateToLocalOffset());
DateTime corp_now=utc_now.AddMinutes(-8*60);
string pattern=cntx.GetUIDatePattern("short_date");
string corp_now_string=corp_now.ToString(pattern);
if (!res1.isError())
{
	Item qry2=this.newItem("Header");
	qry2.setProperty("date",corp_now_string);
	res1.appendItem(qry2);
}
return res1;
