Item.method_code:
string qry1=@"
<AML>
<Item type='PackageDefinition' action='get'>
  <Relationships>
    <Item type='PackageGroup' action='get'>
      <Relationships>
        <Item type='PackageElement' action='get' >
          <element_type>{0}</element_type>
          <name>{1}</name>
        </Item>
      </Relationships>
    </Item>
  </Relationships>
</Item>
</AML> 
";
qry1=string.Format(qry1,this.getProperty("packagegroup",""),this.getProperty("element_name",""));
Item res=this.getInnovator().applyAML(qry1);
if (!res.isError()&&!res.isEmpty()&&!res.isCollection())
{
   this.setProperty("packagedefinition",res.getID());
}
return this;
