Item.method_code:
try {

	//var q=document.getElementsByName("quarters").item().value;
	var q=document.thisItem.getProperty("quarters","");

	var icon="../customer/images/Quarters";
	if (q)
	{
	  icon+=q+".gif";
	  handleItemChange("quarters_img",icon);
	}
	else
	{
	  handleItemChange("quarters_img","");
	}
}
catch (err) {
	top.aras.AlertSuccess("Image will be updated on save");
}
