Item.method_code:
try {

	var q=document.thisItem.getProperty("rating","");
	var icon="../customer/images/Rating";
	if (q)
	{
	  icon+=q+".gif";
	  handleItemChange("rating_img",icon);
	}
	else
	{

	  handleItemChange("rating_img","");
	}
}
catch (err) {
	top.aras.AlertSuccess("Image will be updated on save");
}
