var eln=this.getProperty("element_name");
var qry1=this.newItem(pg);
qry1.setProperty("keyed_name",eln);
qry1=qry1.apply("get");
if (!qry1.isError()&&qry1.getID())
{
 top.aras.uiShowItem(pg,qry1.getID());
}
else
{
  top.aras.AlertError("Element not found");
}
