Item.method_code:
string id=this.getID();
Item qry1 = this.newItem("SQL","SQL PROCESS");
qry1.setProperty("name","userstory_gettreemapdata");
qry1.setProperty("PROCESS","CALL");
qry1.setProperty("ARG1",id );
Item res = qry1.apply();
if (res.isError()) {return res;	}
string treemap_data="";
for (int i=0;i<res.getItemCount();i++)
{ 
  treemap_data+=res.getItemByIndex(i).getProperty("story_data");
}
return this.getInnovator().newResult(treemap_data);
