Item.method_code:
if (!inArgs.itemContext) {return;}
var context_item=this.newItem();
context_item.loadAML(inArgs.itemContext.xml);
var source_id=context_item.getID();
var qry1 = this.newItem("UserStoryElement");
qry1.setProperty("source_id",source_id);
var res= qry1.apply("get");
if (res.isEmpty()) {return "";}
if (res.isError()) {top.aras.AlertError(res.getErrorString());}
var count=res.getItemCount();
var idarray=new Array();
for (i=0;i<count;i++){
  var item=res.getItemByIndex(i);
  idarray[i]=item.getID();
  var idlist=idarray.join(",");
}
inArgs.QryItem.item.setAttribute("idlist",idlist);
return("");
